  public function consultarInformeGestion()

  {
  
   $ano=$_GET['nombre'];    

    $listar = $this->db->prepare("select ig.id,ig.ano,juz.nombre as juzgado,are.nombre as area,ig.ano,ig.enero,ig.febrero,ig.marzo,ig.abril,ig.mayo,ig.junio,ig.julio,
