		$date   = date_create($nuevafecha);
		$fechat = date_format($date,'Y-n-j');
						
		$fecha = explode("-",$fechat);
						
		$year  = $fecha[0];
		$month = $fecha[1];
		$day   = $fecha[2];
		
		$date_2 = date('D', mktime(0,0,0,$month,$day,$year));
				
