				$tablas    = 'expe_solicitud';
				$tipo      = 'RESSDIGI';
				//$cadena_archivos = "MUCHOS";
				$this->db->exec("INSERT INTO expe_historial(idda,idusuario,fecha,hora,actuacion,tablas,tipo)
