				$msg = "PROCESO SE REALIZA CORRECTAMENTE";
				
				
															
			//SE TERMINA LA TRANSACCION  
			$this->db->commit();
											
			echo $msg;
								
						
						  
		} 
		catch (Exception $e) {
						
							
			$msg = "ERROR EN PROCESO: ".$e->getMessage();
							
							
			//NO TERMINA LA TRANSACCION SE PRESENTO UN ERROR
			$this->db->rollBack();
							
						
			echo $msg;
							
							
		}
		
				
  	}
	
	
	
